INSERT INTO PERSONAJES VALUES (1, 1, 'https://i2.wp.com/codigoespagueti.com/wp-content/uploads/2020/04/Anna-Frozen.jpg?resize=1200%2C720&quality=80&ssl=1', 'Princesa Anna de Arendelle', 18, 65.2, 'La idea de crear una adaptación del cuento de "La Reina de las Nieves" se dio por primera vez en los estudios Disney en 1943, cuando Walt Disney consideró colaborar con Samuel Goldwyn para producir una película biográfica de Hans Christian Andersen.');
INSERT INTO PERSONAJES VALUES (2, 1, 'https://www.xtrafondos.com/wallpapers/vertical/reina-de-hielo-elsa-en-frozen-391.jpg', 'Elza de Arendelle', 22, 65.5, 'Elsa la Reina de Arendelle, es la protagonista principal de la película de animación de Disney ganadora del Oscar 2013 Frozen.');
INSERT INTO PERSONAJES VALUES (3, 2, 'https://tvazteca.brightspotcdn.com/28/45/43f7d86744e19093dd53b3b4a7cc/encanto-mirabel.jpg', 'Maribel Madrigal', 15, 50.5, 'Mirabel es una joven de 15 años, de ascendencia colombiana, piel trigueña con ojos color avellana y cabello negro rizado, con pocas pecas en la nariz y mejillas.');
INSERT INTO PERSONAJES VALUES (4, 2, 'https://img.culturacolectiva.com/content_image/2022/1/26/1643221283926-luisa_madrigal_encanto.jpg', 'Luisa Madrigal', 18, 70.2, 'Luisa Madrigal es un personaje de la próxima película de Disney, Encanto. Es la hija mediana de Julieta Madrigal y Agustín Madrigal, dotada de super fuerza.');